    class Solution:
		def longestSubstring(self, s: str, k: int) -> int:
			return self.check(s, k)

		def check(self, s, k):
			if len(s) == 0:
				return 0
			d = {}
			for l in s:
				try:
					d[l] += 1
				except:
					d[l] = 1
			stop = []
			for key in d.keys():
				if d[key] < k:
					stop.append(key)
			if len(stop) == 0:
				return len(s)
			valid_s = []
			start = 0
			for i in range(len(s)):
				if s[i] in stop:
					valid_s.append(s[start:i])
					start = i+1
			valid_s.append(s[start:])
			max_v = 0
			for vs in valid_s:
				if len(vs) == 0:
					continue
				tmp = self.check(vs, k)
				if tmp > max_v:
					max_v = tmp
			return max_v

