class Solution:
    def longestSubstring(self, s, k):
        #prefix holds prefix sums for each character
        prefix, tmp = defaultdict(list), defaultdict(int)
        for c in s:
            tmp[c] += 1
            for i in range(97,123):
                ci = chr(i)
                prefix[ci].append(tmp[ci])

        ans = 0 #answer of the question
        last = defaultdict(int) #last seen position of each character so far
        for i,c in enumerate(s):
            last[c] = i #update last seen position for the current character
            #get rid of all characters that have frequency < k
            left = -1
            for j in range(97,123):
                cj = chr(j)
                if 0 < prefix[cj][i] < k:
                    left = max(left, last[cj]) #letter violates frequency requirement, update left
            #now you are left with string[left+1:i]
            #check if this substring satisfies the frequenct requirement
            flag = True
            for j in range(97,123):
                cj = chr(j)
                if 0 < prefix[cj][i]-prefix[cj][left] < k:
                    flag = False #frequency condition violated
                    break
            if flag: 
                ans = max(ans, i-left) #this is a valid substring, compare to the current ans
        return ans
