                for (int c = 0; c < 26; c++) {
                    int num = letters[c][end] - letters[c][start];
                    if (num < k && num > 0) {
                        works = false;
                        end = s.indexOf('a' + c, start) + 1;
                        break;
                    }
                }
                if (works) {
                    if (end - start > longest) longest = end - start;
                    break;
                }
            }
        }

        return longest;
    }
}
