            if (!count[str[i] - 'a']) numberOfUniqueChar++;
            count[str[i] - 'a']++;
        }

        // longest substring length
        int result = 0;

        // Now, find longest substring for each number of unique characters
        // i.e longest substring, where number of unique characters lies from
        // [1, numberOfUniqueChar]
        for (int n = 1; n <= numberOfUniqueChar; n++) { // can be atmax 26
            // store the count of n unique characters
            int count[26] = {0};
            // count how many unique characters are there
            int countUniqueChar = 0;
            // count how many unique character occurs >= K times
            int charHasK = 0;
            // sliding window
            int l = 0, h = 0;
            while (h < str.size()) {
                // if number of unique character is <= n
                if (countUniqueChar <= n) {
                    int indx = str[h] - 'a';
                    if (count[indx] == 0) countUniqueChar++;
                    count[indx]++;
                    if (count[indx] == k) charHasK++;
                    // move end pointer to right
                    h++;
                } else {
                    int indx = str[l] - 'a';
                    if (count[indx] == k) charHasK--;
                    count[indx]--;
                    if (count[indx] == 0) countUniqueChar--;
                    // move start pointer to right
                    l++;
                }
                // satisfies the condition for question
                if (countUniqueChar == n && charHasK == n) {
                    result = max(result, h - l);
                }
            }
        }

        return result;
    }
};

