class Solution {
    public int longestSubstring(String s, int k) {
        if (s == null || s.length() == 0) return 0;
        int maxLength = Integer.MIN_VALUE;
        for (int i = 1; i <= 26; i++) {
            maxLength = Math.max(maxLength, getLongest(i, k, s));
        }
        return maxLength;
    }
    private int getLongest(int num, int k, String s) {
        int left = 0, right = 0, count = 0, times = 0;
        int[] map = new int[128];
        int maxLen = Integer.MIN_VALUE;
        while (right < s.length()) {
            if (map[s.charAt(right)]++ == 0) count++;
            if (map[s.charAt(right++)] == k) times++;
            while (count > num) {
                if (map[s.charAt(left)]-- == k) times--;
                if (map[s.charAt(left++)] == 0) count--;
            }
            if (times == num && times == count) {
                maxLen = Math.max(maxLen, right - left);
            }
        }
        return maxLen == Integer.MIN_VALUE ? 0 : maxLen;
    }
}
