        counter = collections.Counter(s)
        if all(counter[i] >= k for i in counter):
            return len(s)

        start, longest = 0, 0
        for i in xrange(len(s)):
            if counter[s[i]] < k:
                longest = max(longest, self.longestSubstring(s[start:i], k))
                start = i + 1

        longest = max(longest, self.longestSubstring(s[start:], k))
        return longest
