class Solution(object):
    def longestSubstring(self, s, k):
        """
        :type s: str
        :type k: int
        :rtype: int
        """
        if len(s) < k:
            return 0
        mydict, myset = {}, set()
        for c in s:
            if c in mydict.keys():
                mydict[c] += 1
            else:
                mydict[c] = 1
            if mydict[c] >= k:
                myset.discard(c)
            else:
                myset.add(c)
        if len(myset) == 0:
            return len(s)
        intervals, start = [], 0
        while start < len(s):
            if s[start] not in myset:
                i = start
                while start < len(s):
                    if s[start] not in myset:
                        start += 1
                    else:
                        break 
                intervals.append((i, start))
            else:
                start += 1
        gMax = 0
        for interval in intervals:
            gMax = max(gMax, self.longestSubstring(s[interval[0]:interval[1]], k))
        return gMax
