class Solution {     
  int search(string s,int k){
        if(s.size() < k)
            return 0;
        unordered_map<char,int> count_map;
        for(int i = 0;i < s.length(); i++)
            count_map[s[i]]++;
        for(auto item: count_map)
            if(item.second < k){
                int char_index = s.find(item.first);
                return max(search(s.substr(0,char_index),k), search(s.substr(char_index+1),k));
            }
        return s.size();
    }       
  public:
    int longestSubstring(string s, int k) {
        return search(s,k);
    }      
};
```

