        for (int i = start; i < end; i++) hist[s[i] - 'a']++;
        for (int i = start; i < end; i++) {
            if (hist[s[i] - 'a'] < k) { // split upon not good character
                if (i - start > longest) longest = longest(s, start, i, k, longest);
                while (i < end && hist[s[i] - 'a'] < k) i++; // skip all not good characters
                if (end - i > longest) longest = longest(s, i, end, k, longest);
                return longest;
            }
        }
        return end - start; // if we didn't exit earlier it means all characters in fragment are good
    }
}
