class Solution {
    public int longestSubstring(String s, int k) {
        char[] strn=s.toCharArray();
        return longestSubstring(strn,0,s.length()-1,k);      
    }
    private int longestSubstring(char[] strn,int start, int end, int frequency){
        int N=end-start+1;
        int s=start;
        int e=end;
        int k=frequency;

        if(s==e && k==1) return 1;
        if(N<k) return 0;

        int[] FrequencyArray=new int[26];
		//counting the occurences... 

        for(int i=s;i<=e;i++){
            char ch=strn[i];
            int index=ch-'a';
            FrequencyArray[index]+=1;
        }

        boolean Condition_Satisfied=true;

        for(int i=s;i<=e;i++){
            char ch=strn[i];
            int index=ch-'a';
            if(FrequencyArray[index]<k){
                Condition_Satisfied=false;
				break;
            }
        }

        if(Condition_Satisfied==true){
		//if the entire string satisifies the condition
            return N;
        }

        int Res=0;
        int i=s;
        int j=s;

        while(j<=e){
            char ch=strn[j];
            int f=FrequencyArray[ch-'a'];
			//if the frequency is not k then we this character to split
            if(f<k){
                Res=Math.max(Res,longestSubstring(strn,i,j-1,k));
				//idea is in substring(i,j) there is no character ch
                i=j+1;
            }
            j=j+1;
        }

        return Math.max(Res,longestSubstring(strn,i,e,k));
		//also the take maximum of the last left string...

    }
}
