            if(last.empty()) last={i, i+duration};  // case 1: 1st segment
            else if(i <=last[1]) last[1] =  max(last[1], i+duration); // case 2: overlapping segment
            else ans += last[1] - last[0], last={i, i+duration}; // case 3: non-overlapping segment
        }
        if(!last.empty())  ans += last[1] - last[0]; // if there is unprocessed.
        return ans;
    }
};
