class Solution {
public:
    int findPoisonedDuration(vector<int>& timeSeries, int duration) {
        int sum = 0, end = -1;

        for(int i = 0; i <timeSeries.size(); i++){
            //if the time interval collides
            //exclude the common interval 
            //as it is already included in the answer
            if(end >= timeSeries[i])
                sum += duration - (end - timeSeries[i]+1);
            else
            sum += duration; //if no collision,simply add the whole duration
            end = timeSeries[i] + duration - 1; //keep updating end time
        }

        return sum;
    }
};
