int findPoisonedDuration(vector<int>& timeSeries, int duration) {
        int res = 0;
        for(auto it = timeSeries.begin(); it != timeSeries.end(); ) {
            auto e = lower_bound(it + 1, timeSeries.end(), *it + duration);
