```
class Solution {

    fun findPoisonedDuration(timeSeries: IntArray, duration: Int): Int {
        val timelines = mutableListOf<Pair<Int, Int>>() // from, to
        for (attachedAt in timeSeries) {
            if (timelines.isEmpty()) {
                timelines.add(Pair(attachedAt, attachedAt + duration - 1))
            } else {
                val previous = timelines.last()
                if (previous.second < attachedAt) {
                    timelines.add(Pair(attachedAt, attachedAt + duration - 1))
                } else {
                    timelines[timelines.lastIndex] = previous.copy(second = attachedAt + duration - 1)
                }
            }
        }

        var total = 0
        for ((start, end) in timelines) {
            total += end - start + 1
        }
        return total
    }
}
```

