class Solution {
public:
    int findPoisonedDuration(vector<int>& arr, int k) {
        int count = 0;
        if(arr.size() == 0) return 0;
        if(arr.size() == 1) return k;
        for(int i = 1;i < arr.size();i++){
            if(arr[i] - arr[i-1] < k) count += (arr[i] - arr[i-1]);
            else count += k;
        }
        count += k;
        return count;
    }
};
