            for(int i1 = 1; i1 <= n1; i1++) dp[i1][0] = dp[i1 - 1][0] && s1[i1 - 1] == s3[i1 - 1];

            for(int i1 = 1; i1 <= n1; i1++){
                for(int i2 = 1; i2 <= n2; i2++){
                    dp[i1][i2] = (dp[i1 - 1][i2] && s1[i1 - 1] == s3[i1 + i2 - 1]) || (dp[i1][i2 - 1] && s2[i2 - 1] == s3[i1 + i2 - 1]);
                }
            }

            return dp[n1][n2];  
        }
    };

