class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {
        int n=s1.length();
        int m=s2.length();
        if(n+m!=s3.length())
            return 0;
        bool dp[n+1][m+1];
        memset(dp,0,sizeof(dp));

        for(int i=0;i<=n;i++)
        {
            for(int j=0;j<=m;j++)
            {
                if(i==0&&j==0)
                    dp[i][j]=true;
                else if(i==0)
                {
                    if(s2[j-1]==s3[j-1])
                        dp[i][j]=dp[i][j-1];
                }
                else if(j==0)
                {
                    if(s1[i-1]==s3[i-1])
                        dp[i][j]=dp[i-1][j];
                }
                else if(s1[i-1]==s3[i+j-1]&&s2[j-1]!=s3[i+j-1])
                    dp[i][j]=dp[i-1][j];

                else if(s1[i-1]!=s3[i+j-1]&&s2[j-1]==s3[i+j-1])
                    dp[i][j]=dp[i][j-1];

                else if(s1[i-1]==s3[i+j-1]&&s2[j-1]==s3[i+j-1])
                    dp[i][j]=(dp[i-1][j] || dp[i][j-1]);
            }
        }
        return dp[n][m];
    }
};
