```C# []
public class Solution {
    public bool IsInterleave(string s1, string s2, string s3) {
        if (s1.Length + s2.Length != s3.Length) {
            return false;
        }

        bool[,] dp = new bool[s1.Length + 1, s2.Length + 1];
        dp[s1.Length, s2.Length] = true;

        for (int i = s1.Length; i >= 0; i--) {
            for (int j = s2.Length; j >= 0; j--) {
                if (i < s1.Length && s1[i] == s3[i + j] && dp[i + 1, j]) {
                    dp[i, j] = true;
                }
                if (j < s2.Length && s2[j] == s3[i + j] && dp[i, j + 1]) {
                    dp[i, j] = true;
                }
            }
        }
        return dp[0, 0];
    }

    private Dictionary<(int, int), bool> dp = new Dictionary<(int, int), bool>();
    private bool DFS(int i, int j, string s1, string s2, string s3) {
        if (i == s1.Length && j == s2.Length) {
            return true;
        }
        if (dp.ContainsKey((i, j))) {
            return dp[(i, j)];
        }

        bool result = false;
        if (i < s1.Length && s1[i] == s3[i + j] && DFS(i + 1, j, s1, s2, s3)) {
            result = true;
        }
        if (j < s2.Length && s2[j] == s3[i + j] && DFS(i, j + 1, s1, s2, s3)) {
            result = true;
        }

        dp[(i, j)] = result;
        return result;
    }
}
