class Solution {
    public boolean isInterleave(String s1, String s2, String s3) {
        return isInterleaveFrom(s1, s2, s3, 0, 0, 0);
    }

    private boolean isInterleaveFrom(
        String m, String n, String p,
        int mIndex, int nIndex, int pIndex) {
        if (pIndex == p.length())
            return mIndex == m.length() && nIndex == n.length();

        if (mIndex < m.length() && m.charAt(mIndex) == p.charAt(pIndex)) {
            if (isInterleaveFrom(m, n, p, mIndex + 1, nIndex, pIndex + 1))
                return true;
        }
        if (nIndex < n.length() && n.charAt(nIndex) == p.charAt(pIndex)) {
            if (isInterleaveFrom(m, n, p, mIndex, nIndex + 1, pIndex + 1))
                return true;
        }

        return false;
    }
}
