class Solution {
public:
    bool answ(string s1,int i,string s2,int j,string s3,int k,vector<vector<int>> &dp)
    {
        if(i==s1.length())
        {
            string a=s2.substr(j);
            string b=s3.substr(k);

            return a==b;
        }

        if(j==s2.length())
        {
            string a=s1.substr(i);
            string b=s3.substr(k);

            return a==b;
        }

        if(dp[i][j]>=0)
        {
            if(dp[i][j]==1)
                return true;
            return false;
        }

        bool ans=false;

        if((s3[k]==s1[i] and answ(s1,i+1,s2,j,s3,k+1,dp)) or (s3[k]==s2[j] and answ(s1,i,s2,j+1,s3,k+1,dp)))
            ans=true;

        dp[i][j]=ans?1:0;

        return ans;
    }

    bool isInterleave(string s1, string s2, string s3)
    {
        int n=s1.length();
        int m=s2.length();
        vector<vector<int>> dp(n,vector<int>(m,-1));

        return answ(s1,0,s2,0,s3,0,dp);
    }
};
