bool mem(string &a, string &b, string &ist, vector<vector> &dp, int i = 0, int j = 0, int k = 0)
{
int n = a.length(), m = b.length();
if (n + m != ist.length())
return false;
if (i == n && j == m)
    return true; // base case

if (dp[i][j] != -1)
    return dp[i][j];

bool left, right;
left = i < n ? a[i] == ist[k] : false;
right = j < m ? b[j] == ist[k] : false;

if (left && right)
    return dp[i][j] = mem(a, b, ist, dp, i + 1, j, k + 1) || mem(a, b, ist, dp, i, j + 1, k + 1);

else if (left)
    return dp[i][j] = mem(a, b, ist, dp, i + 1, j, k + 1);

else if (right)
    return dp[i][j] = mem(a, b, ist, dp, i, j + 1, k + 1);

else
    return dp[i][j] = false;
}
