bool tabulation(string& a,string& b,string& ist)
{
int m = b.length(), n = a.length();
if (m + n != ist.length())
return false;
vector<vector<bool>> dp(n+1,vector<bool>(m+1,true));

for (int j = m - 1; j >= 0; j--)
{
    if (b[j] == ist[n + j])
        dp[n][j] = dp[n][j+1];
    else
        dp[n][j] = false;
} // base case i==n

for (int i = n - 1; i >= 0; i--)
{
    dp[i][m] = a[i] == ist[m + i];

    for (int j = m - 1; j >= 0; j--)
    {
        bool left, right;
        left = a[i] == ist[i + j];
        right = b[j] == ist[i + j];

        if (left && right)
            dp[i][j] = dp[i+1][j] || dp[i][j+1];

        else if (right)
            dp[i][j] = dp[i][j+1];

        else if(left)
            dp[i][j]=dp[i+1][j];

        else
            dp[i][j] = false;
    }
}

return dp[0][0];
}
bool isInterleave(string s1, string s2, string s3)
{
return tabulation(s1, s2, s3);
}

