```
class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {
        int m = s1.length();
        int n = s2.length();

        // Check if the lengths of s1, s2, and s3 add up
        if (m + n != s3.length()) {
            return false;
        }

        // Create a 2D DP array to store the intermediate results
        vector<vector<bool>> dp(m + 1, vector<bool>(n + 1, false));

        // Initialize the base cases
        dp[0][0] = true;

        // Check if s1 matches with s3
        for (int i = 1; i <= m; i++) {
            if (s1[i - 1] == s3[i - 1] && dp[i - 1][0]) {
                dp[i][0] = true;
            }
        }

        // Check if s2 matches with s3
        for (int j = 1; j <= n; j++) {
            if (s2[j - 1] == s3[j - 1] && dp[0][j - 1]) {
                dp[0][j] = true;
            }
        }

        // Check if s1 and s2 interleave to form s3
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if ((s1[i - 1] == s3[i + j - 1] && dp[i - 1][j]) ||
                    (s2[j - 1] == s3[i + j - 1] && dp[i][j - 1])) {
                    dp[i][j] = true;
                }
            }
        }

        return dp[m][n];
    }
};

```

