        int len1 = s1.length(), len2 = s2.length(), len3 = s3.length();
        vector dp(len3 + 1, vector<vector<int>>(len1 + 1, vector<int>(len2 + 1, -1)));
        return canInterleave(0, 0, 0, s1, s2, s3, dp);
