        vector curdp(len1 + 1, vector<bool>(len2 + 1, false));
        vector nextdp(len1 + 1, vector<bool>(len2 + 1, false));

        nextdp[len1][len2] = true;

        for(int k = len3 - 1; k >= 0; k--) {
