    class Solution {
    public boolean isInterleave(String s1, String s2, String s3) {
        if(s1.length()+s2.length()!=s3.length())return false;
        int dp[][][]=new int[s1.length()+1][s2.length()+1][s3.length()];
        for(int p[][]:dp){
            for(int t1[]:p){
                Arrays.fill(t1,-1);
            }
        }
        return f(0,0,0,s1,s2,s3,dp);
    }
    public boolean f(int i,int j,int k,String s1,String s2,String s3,int dp[][][]){
        if(i>=s1.length() && j>=s2.length() && k>=s3.length())return true;

        if(dp[i][j][k]!=-1)return dp[i][j][k]==0?false:true;
        boolean up=false;
        //checking if from both string characters are equal
        if((i<s1.length() && s1.charAt(i)==s3.charAt(k)) && (j<s2.length() && s2.charAt(j)==s3.charAt(k))){
            boolean take=f(i+1,j,k+1,s1,s2,s3,dp);
            boolean not=f(i,j+1,k+1,s1,s2,s3,dp);
            up=take || not;
        }
         // checking if from first string characters are equal  
        else if(i<s1.length() && s1.charAt(i)==s3.charAt(k)){
            up=f(i+1,j,k+1,s1,s2,s3,dp);
        }
        //checking if from second string characters are equal  
        else if(j<s2.length() && s2.charAt(j)==s3.charAt(k)){
            up=f(i,j+1,k+1,s1,s2,s3,dp);
        }
        dp[i][j][k]=(up==false)?0:1;
        return up;
    }
}

