```
class Solution {
public:
    // Recursive DP Solution

    vector < vector < int > > dp;

    bool isInterleave(string s1, string s2, string s3) {
        dp.assign(s1.size() + 1, vector < int > (s2.size() + 1, -1));
        return ans(s1, s2, s3, 0, 0, 0);    
    }

    bool ans(string s1, string s2, string s3, int idx1, int idx2, int idx3){
	   if(idx3 == s3.size() && idx1 == s1.size() && idx2 == s2.size()) return true;
        if(dp[idx1][idx2] != -1) return dp[idx1][idx2];
        bool res = false;
        if(s1[idx1] == s3[idx3] && idx1 < s1.size())
            res |= ans(s1, s2, s3, idx1 + 1, idx2, idx3 + 1);
        if(s2[idx2] == s3[idx3] && idx2 < s2.size())
            res |= ans(s1, s2, s3, idx1, idx2 + 1, idx3 + 1);
        return dp[idx1][idx2] = res;
    }
};
