class Solution {
    public boolean isInterleave(String s1, String s2, String s3) {
        int m = s1.length();
        int n = s2.length();
        if(m + n != s3.length()) return false;
        boolean[] next = new boolean[n+1];
        next[n] = true;
        for(int i = m-1; i >= 0; i--) {
            boolean[] curr = new boolean[n+1];
            for(int j = n; j >= 0; j--) {
                if(i < s1.length() && s1.charAt(i) == s3.charAt(i+j) && next[j]) {
                    curr[j] = next[j];
