class Solution {
public:
    bool isInterleave(string s1, string s2, string s3) {

        int n1 = s1.size();
        int n2 = s2.size();
        int n3 = s3.size();
        if (n1 + n2 != n3) {
            return false;
        }

        queue<char> q1,q2;

        for(int i=0;i<n1;i++){
            q1.push(s1[i]);
        }
        for(int i=0;i<n2;i++){
            q2.push(s2[i]);
        }

        int i=0;
        while(!q1.empty() && !q2.empty() && i< n3){
            while(i<n3 && s3[i]==q1.front()){
                q1.pop();
                i++;
            }
            while(i<n3 && s3[i]==q2.front()){
                q2.pop();
                i++;
            }
            if(s3[i]!=q1.front() && s3[i]!=q2.front()){
                return false;
            }
        }
        while(!q1.empty() && i<n3){
            if(s3[i]==q1.front()){
                i++;
                q1.pop();
            }
            else return false;
        }
        while(!q2.empty() && i<n3){
            if(s3[i]==q2.front()){
                i++;
                q2.pop();
            }
            else return false;
        }
        return q1.empty() && q2.empty() && i == n3;
    }
};
