/*
 * Encodes a tree to a single string.
 *
 * @param {TreeNode} root
 * @return {string}
 */
var serialize = function(root) {
    let string = '';

    function buildString(node) {
        if (!node) {
            string += 'e ';
        } else {
            string += node.val + ' ';
            buildString(node.left);
            buildString(node.right);
        }
    }

    buildString(root);

    return string;
};
