    TreeNode* helper_deserialize(queue<string> &q) {
        if (q.empty()) return NULL;
        string s = q.front();
        q.pop();
        if (s == "null") return NULL;
        int num = stoi(s);
        // int num = 10;
        TreeNode* node = new TreeNode(num);
        node -> left = helper_deserialize(q);
        node -> right = helper_deserialize(q);
        return node;
