var serialize = function(root) {
  const answer = [];
  function serialize(node) {
    if (!node) return answer.push(null);
    serialize(node.right);
    serialize(node.left);
    answer.push(node.val);
  }
  serialize(root);
  //Answer array is reversed postorder.  That way
  //I can .pop() in the next part instead of .shift()
  //since .shift is an O(n) operation on its own
  //due to the fact that it has to reindex the
  //rest of the array.  Also, if we want to return
  //it as a string, we can .join(',') and the .split(',')
  //after it has been passed.  Or, JSON.stringify()
  //and JSON.parse() work too...  But they work for
  //the entire object, so I'm not sure where the line 
  //for cheating is on this one. lol
  return answer;
};

var deserialize = function(data) {
  function build() {
    const curVal = data.pop();
    if (curVal === null) return null;
    const node = new TreeNode(curVal);
    node.left = build();
    node.right = build();
    return node;
  }
  return build();
};
```

