```
public class Codec {

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        StringBuilder sb = new StringBuilder();
        Queue<TreeNode> q = new LinkedList<>();
        q.add(root);
        while(!q.isEmpty()){
            TreeNode curr = q.poll();
            if (curr == null){
                sb.append("null ");
            } else {
                sb.append(curr.val + " ");
                q.add(curr.left);
                q.add(curr.right);
            }
        }
        return sb.toString();
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        String[] d = data.split(" ");
        if (d[0].equals("null")) return null;
        TreeNode root = new TreeNode(Integer.parseInt(d[0]));
        Queue<TreeNode> q = new LinkedList<>();
        q.add(root);
        int i = 0;
        while(i < d.length - 1){
            TreeNode curr = q.poll();
            if (!d[++i].equals("null")){  //must be ++idx other than idx++ in the next line;
                curr.left = new TreeNode(Integer.parseInt(d[i]));
                q.add(curr.left);
            }
            if (!d[++i].equals("null")){
                curr.right = new TreeNode(Integer.parseInt(d[i]));
                q.add(curr.right);
            }
        }
        return root;        
    }
}
