public class Codec {

    private String del = ",";
    private String nil = "nil";

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        StringBuilder sb = new StringBuilder();
        serial(sb, root);
        return sb.toString();
    }

    private void serial(StringBuilder sb, TreeNode node) {
        // System.out.println(sb.toString());
        if (node  == null) {   // check null as DFS terminator
            sb.append(nil);
            return;
        }
        sb.append(node.val).append(del);
        serial(sb, node.left);
        sb.append(del);
        serial(sb, node.right);
        System.out.println(sb.toString());
    }
    // Decodes your encoded data to tree.
    int idx;
    public TreeNode deserialize(String data) {
        String[] ss = data.split(del);
        idx = 0;
        return ds(ss);
    }

    private TreeNode ds(String[] ss) {
        if (idx >= ss.length) return null;
        String next = ss[idx++];
        if (next.equals(nil)) return null;  // check null as DFS terminator
        // System.out.println(next);
        TreeNode root = new TreeNode(Integer.valueOf(next));
        root.left = ds(ss);
        root.right = ds(ss);
        return root;
    }
}
