public class Codec {

    private String del = ",";
    private String nil = "nil";

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        if (root == null) return "";
        Stack<TreeNode> st = new Stack<>();
        TreeNode curr = root;
        StringBuilder sb = new StringBuilder();
        while(curr != null || !st.isEmpty()) {
            if (curr != null) {
                sb.append(String.valueOf(curr.val)).append(del);
                st.push(curr);
                curr = curr.left;
            } else {
                sb.append(nil).append(del);
                curr = st.pop();
                curr = curr.right;
            }
        }
        System.out.println(sb.toString());
        return sb.toString();
