    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        if (data == null || data.length() == 0) return null;
        String[] ss = data.split(del);
        int idx = 0;
        Stack<TreeNode> st = new Stack<>();
        if (ss[idx].equals(nil)) return null;
        TreeNode root = new TreeNode(Integer.valueOf(ss[idx++]));
        st.push(root);
        TreeNode curr = root;
        while (idx < ss.length) {
            while (idx < ss.length && !ss[idx].equals(nil)) {
                curr.left = new TreeNode(Integer.valueOf(ss[idx++]));
                curr = curr.left;
                st.push(curr);
            }
            while (idx < ss.length && ss[idx].equals(nil) && !st.isEmpty()) {
