```
public class Codec {

    private static final String nStr = "null";

    // Encodes a tree to a single string.
    // TC -> O(n)
    // SC -> O(Max Breadth of tree) for que + O(n) for result
    public String serialize(TreeNode root) {
        if (root == null)
            return "";
        StringBuilder sb = new StringBuilder();

        Deque<TreeNode> que = new LinkedList<>();
        que.addLast(root);

        while (!que.isEmpty()) {
            TreeNode node = que.removeFirst();

            if (node == null) {
                sb.append(nStr).append(",");
                continue;
            }

            sb.append(node.val).append(",");
            que.addLast(node.left);
            que.addLast(node.right);
        }

        sb.setLength(sb.length() - 1);

        System.out.println(sb.toString());

        return sb.toString();
    }

    // Decodes your encoded data to tree.
    // TC -> O(n)
    // SC -> O( Max Breadth of Tree) for BFS que + O(n) for result
    public TreeNode deserialize(String data) {
        if (data == null || data.length() == 0)
            return null;

        String[] dTokens = data.split(",");

        TreeNode root = new TreeNode(Integer.parseInt(dTokens[0]));
        int cnt = 1;

        Deque<TreeNode> que = new ArrayDeque<>();
        que.addLast(root);

        while(!que.isEmpty() && cnt < dTokens.length) {
            TreeNode node = que.removeFirst();

            String left = dTokens[cnt++];
            if (!left.equals(nStr)) {
                TreeNode lNode = new TreeNode(Integer.parseInt(left));
                node.left = lNode;
                que.addLast(lNode);
            }

            String right = dTokens[cnt++];
            if (!right.equals(nStr)) {
                TreeNode rNode = new TreeNode(Integer.parseInt(right));
                node.right = rNode;
                que.addLast(rNode);
            }
        }

        return root;
    }
}

