Iterative:
```
class Codec {
public:

	// Encodes a tree to a single string.
	string serialize(TreeNode* root)
	{
		if (root == nullptr) return "null";
		string str;
		queue<TreeNode*> q;
		q.push(root);
		while (!q.empty())
		{
			TreeNode* node = q.front();
			q.pop();
			str += (node ? ::to_string(node->val) : "null") + ' ';
			if (node)
			{
				q.push(node->left);
				q.push(node->right);
			}
		}
		return str;
	}

	// Decodes your encoded data to tree.
	TreeNode* deserialize(string data)
	{
		TreeNode* head = nullptr;
		queue<TreeNode*> q;
		stringstream ss(data);
		string s;
		if (ss >> s && s != "null")
		{
			head = new TreeNode(::stoi(s));
			q.push(head);
		}
		while (!q.empty())
		{
			TreeNode* node = q.front();
			q.pop();
			if (ss >> s && s != "null")
			{
				node->left = new TreeNode(::stoi(s));
				q.push(node->left);
			}
			if (ss >> s && s != "null")
			{
				node->right = new TreeNode(::stoi(s));
				q.push(node->right);
			}
		}
		return head;
	}
};
```

Recursive:
