private string[] Split(string s, char sep)
{
	var items = new List<string>();

	for (int i = 0, end; i < s.Length && ((end = s.IndexOf(',', i)) != -1 || (end = s.Length) >= 0); i = end + 1)
	{
		items.Add(s.Substring(i, end - i));
	}
	return items.ToArray();
}
