  *   2   3
  *      / \
  *     4   5
  * ==> #,#,2,#,#,4,#,#,5,3,1
  */
class Codec {
private:
    TreeNode* deserializeHelper(string& data) {
        if (data.back() == '#') {
            data.pop_back();
            if (data.size())
                data.pop_back();
            return nullptr;
        }
        int n = 0;
        for (int i = data.size() - 1, j = 1; i >= 0 && data[i] != ','; i--, j *= 10) {
