                n = -n;                
            else
                n += (data[i] -'0') * j;
            data.pop_back();
        }
        if (data.size())
            data.pop_back();
        TreeNode* root = new TreeNode(n);
        root->right = deserializeHelper(data);
        root->left = deserializeHelper(data);
