    public class Codec {

        // Encodes a tree to a single string.
        public String serialize(TreeNode root) {
            if(root == null){
                return "null";
            }

            StringBuffer serTree = new StringBuffer();
            Queue<TreeNode> queue = new LinkedList<TreeNode>();
            queue.add(root);
            TreeNode node;

            while(!queue.isEmpty()){
                node = queue.poll();
                if(node.val == Integer.MIN_VALUE){
                    serTree.append("null,");
                    continue;
                }
                else{
                    serTree.append(node.val+",");
                }

                if(node.left != null){
                    queue.add(node.left);
                }
                else{
                    queue.add(new TreeNode(Integer.MIN_VALUE));
                }

                if(node.right != null){
                    queue.add(node.right);
                }
                else{
                    queue.add(new TreeNode(Integer.MIN_VALUE));
                }
            }

            return serTree.toString();
        }

        // Decodes your encoded data to tree.
        public TreeNode deserialize(String data) {
            if(data == null || data.isEmpty() || data.startsWith("null")){
                return null;
            }

            String[] nodes = data.split(",");
            if(nodes.length == 0){
                return null;
            }   

            TreeNode root = new TreeNode(0);
            Queue<TreeNode> queue = new LinkedList<TreeNode>();
            queue.add(root);

            TreeNode node;
            int i = 0;
            while(!queue.isEmpty()){
                node = queue.poll();
                node.val = Integer.parseInt(nodes[node.val]);

                int left = i+1;
                int right = i+2;

                if(left < nodes.length-1 && !nodes[left].equals("null")){
                    TreeNode leftNode = new TreeNode(left);
                    node.left = leftNode;
                    queue.add(leftNode);
                }
                if(right < nodes.length-1 && !nodes[right].equals("null")){
                    TreeNode rightNode = new TreeNode(right);
                    node.right = rightNode;
                    queue.add(rightNode);
                }

                i+=2;
            }

            return root;
        }
    }

