class Solution {
public:

    int integerReplacement(int num) {

     int op = 0;
     long long n = num; 
        while(n>1){

            if(n==3){
                op = op+2;
                break;
            }
            if(n%2==0){
                n = n>>1;

            }
            else{
                long long n1 = (n+1)/2;

                if(n1%2==0){
                    n = n+1;
                }
                else{
                    n = n-1;
                }
            }
              ++op;
        }

        return op;

    }
};


