class Solution {
public:

    int intergerRep(long long n,unordered_map<long long,long long> &dp){
        //Base cases
        if(n==1){
            return 0;
        }
        if(n==0){
            return 1;
        }

        //check if already calculated
        if(dp[n]){
            //if calculated then return it
            return dp[n];
        }

        //if not calculated then calculate using choices

        //choices
        if(n%2==0){//if even
            return  dp[n] = 1+ intergerRep(n/2,dp);
        }else{//if odd
            return  dp[n] = (1 + min(intergerRep(n-1,dp),intergerRep(n+1,dp)));
        }

    }

    int integerReplacement(int n) {

       unordered_map<long long , long long > dp;//to store ovelapping subproblems 

       return intergerRep(n,dp);

    }
};

