class Solution {
    func integerReplacement(_ n: Int) -> Int {
        var res = 0, n = n
        while n != 1 {
            if n % 2 == 0 {
                n /= 2
                // when n is odd we are trying to see which operation removes the most zeros and perform that
                // the last two bits can either be 11 or 01 when the number is odd
                // the other 2 possibilities 00 and 10 are even numbers
                // for 11, adding 1 is better as it would make it 00 and for 01 subtracting 1 would be make 00
                // we can test if the last two bits are 11 by performing an & operation with 3 if the result is 1
                // then the last two bits are 01 
            } else if n == 3 || (n & 3 == 1) {
                n -= 1
            } else {
                n += 1
            }
            res += 1
        }
        return res
    }
}
