    public int helper(int n,int replacements) {
        if (n == 1)
            return replacements;

        if (n % 2 == 1)
            return Math.min(helper(n + 1,replacements + 1),helper(n - 1,replacements + 1));
        else
            return helper(n / 2,replacements + 1);
    }
