class Solution {
public:
    int integerReplacement(int n) {
      map<long long int ,long long int>mpp;
        queue<long long int >q;
        q.push(n);
        while(!q.empty())
        {
            auto t =q.front();
            q.pop();
            if(t%2==0)
            {
                if(!mpp.count(t/2))
                {
                    mpp[t/2] = mpp[t]+1;
                    q.push(t/2);
                }
            }
            else
            {
                if(!mpp.count(t-1))
                {
                    q.push(t-1);
                    mpp[t-1] = mpp[t]+1;
                }
                if(!mpp.count(t+1))
                {
                    q.push(t+1);
                    mpp[t+1]=mpp[t]+1;
                }
            }
            if(mpp.count(0))
            {
                return mpp[0]-1;
            }
        }
        return mpp[0]-1;
    }
};

