class Solution:
    def findRightInterval(self, intervals: List[List[int]]) -> List[int]:
        dic={}
        for i in range(len(intervals)):
            dic[intervals[i][0]]=i

        m=max(dic.keys())

        output=[-1 for _ in range(len(intervals))]

        for i in range(len(intervals)):
            for j in range(intervals[i][1],m+1):
                if dic.get(j,-1)!=-1:
                    output[i]=dic[j]
                    break
        return output

