class Solution {
public:
	int sumOfLeftLeaves(TreeNode* root) {
		if (!root)
			return 0;

		queue<TreeNode*> nodeQueue;
		nodeQueue.push(root);

		int leftLeavesSum = 0;
		TreeNode* currNode;
		while (!nodeQueue.empty()) {
			currNode = nodeQueue.front();
			nodeQueue.pop();

			if (currNode->left) {
				if (!currNode->left->left && !currNode->left->right) { // Check if the left node is a leaf
					leftLeavesSum += currNode->left->val;
				}
				else {
					nodeQueue.push(currNode->left);
				}
			}

			if (currNode->right)
				nodeQueue.push(currNode->right);
		}
		return leftLeavesSum;
	}
};
