class Solution {

    public int sumOfLeftLeaves(TreeNode root) {
        if( root == null || (root.left == null && root.right == null) )
            return 0;
        Queue<TreeNode> q = new LinkedList<>();
        q.add(root);
        int sum = 0;
        while( !q.isEmpty() ){
            int len = q.size();
            for(int i=0;i<len;i++){
                TreeNode cur = q.poll();
                if( cur.left != null ){
                    if( cur.left.left == null && cur.left.right == null )
                        sum += cur.left.val;
                    else
                        q.add(cur.left);
                }
                if( cur.right != null )
                    q.add(cur.right);
            }
        }
        return sum;
    }
}
